mapper.select:
select b.b_section,
b.b_no, b.b_title,
b.b_content, b.b_id, b.b_regdate,
b.b_readcnt
from (
select rownum rn, a.*
from (
select * from pumpkin_board
where b_section
= ${b_section}
order by b_no desc
) a
) b
where rn between
${start} and
${end}

select b.b_no, b.b_title, b.b_content, b.b_id, b.b_regdate, b.b_readcnt
from (
select
rownum rn, a.*
from (
select * from pumpkin_board
where b_section = 1
order by b_no desc
) a
) b
where rn between 1 and 5

select b.b_no, b.b_title, b.b_content, b.b_id, b.b_regdate, b.b_readcnt
from (
select
rownum rn, a.*
from (
select * from pumpkin_board
where b_section = 11
order by b_no desc
) a
) b
where rn between 1 and 5

select b.b_no, b.b_title, b.b_content, b.b_id, b.b_regdate, b.b_readcnt
from (
select
rownum rn, a.*
from (
select * from pumpkin_board
where b_section = 12
order by b_no desc
) a
) b
where rn between 1 and 5

select * from ${tbl_board}
where

	${col_b_id} like #{searchKeyword}


	${col_b_title} like #{searchKeyword}


	${col_b_title} like #{searchKeyword}
	or
	${col_b_content}
	like #{searchKeyword}

order by ${col_b_no} desc

select b.b_no, b.b_title,
b.b_content, b.b_id, b.b_regdate, b.b_readcnt
from (
select rownum rn,
a.*
from (
select * from pumpkin_board
order by b_no desc
) a
) b
where rn
between
#{start} and #{end}
